import React, { useState, useEffect } from 'react';
import api from '../services/api';

const ManagePlans = () => {
  const [plans, setPlans] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [editingPlan, setEditingPlan] = useState(null);
  const [newPlan, setNewPlan] = useState({
    name: '',
    description: '',
    price: '',
    duration_days: '',
    features: ''
  });

  useEffect(() => {
    fetchPlans();
  }, []);

  const fetchPlans = async () => {
    try {
      setLoading(true);
      // Get all plans, including inactive ones
      const response = await api.get('/plans/all');
      setPlans(response.data);
      setError('');
    } catch (err) {
      setError('Failed to fetch plans. ' + (err.response?.data?.message || err.message));
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e, isForEditPlan = false) => {
    const { name, value, type } = e.target;
    const processedValue = type === 'number' ? parseFloat(value) : value;

    if (isForEditPlan && editingPlan) {
      setEditingPlan({
        ...editingPlan,
        [name]: processedValue
      });
    } else {
      setNewPlan({
        ...newPlan,
        [name]: processedValue
      });
    }
  };

  const handleCreatePlan = async (e) => {
    e.preventDefault();
    try {
      await api.post('/plans', {
        ...newPlan,
        price: parseFloat(newPlan.price),
        duration_days: parseInt(newPlan.duration_days, 10)
      });
      setNewPlan({
        name: '',
        description: '',
        price: '',
        duration_days: '',
        features: ''
      });
      fetchPlans();
    } catch (err) {
      setError('Failed to create plan. ' + (err.response?.data?.message || err.message));
    }
  };

  const handleUpdatePlan = async (e) => {
    e.preventDefault();
    try {
      await api.put(`/plans/${editingPlan.id}`, {
        ...editingPlan,
        price: parseFloat(editingPlan.price),
        duration_days: parseInt(editingPlan.duration_days, 10)
      });
      setEditingPlan(null);
      fetchPlans();
    } catch (err) {
      setError('Failed to update plan. ' + (err.response?.data?.message || err.message));
    }
  };

  const handleDeletePlan = async (planId) => {
    if (!window.confirm('Are you sure you want to delete this plan?')) {
      return;
    }

    try {
      await api.delete(`/plans/${planId}`);
      fetchPlans();
    } catch (err) {
      setError('Failed to delete plan. ' + (err.response?.data?.message || err.message));
    }
  };

  const handleToggleActive = async (planId, isCurrentlyActive) => {
    try {
      await api.patch(`/plans/${planId}`, { is_active: !isCurrentlyActive });
      fetchPlans();
    } catch (err) {
      setError('Failed to update plan status. ' + (err.response?.data?.message || err.message));
    }
  };

  const startEditing = (plan) => {
    setEditingPlan(plan);
  };

  const cancelEditing = () => {
    setEditingPlan(null);
  };

  if (loading && plans.length === 0) return <div className="loading">Loading plans...</div>;

  return (
    <div className="manage-plans-container">
      <h2>Manage Subscription Plans</h2>

      {error && <div className="error-message">{error}</div>}

      {!editingPlan ? (
        <div className="plan-form-container">
          <h3>Create New Plan</h3>
          <form onSubmit={handleCreatePlan} className="plan-form">
            <div className="form-group">
              <label>Plan Name</label>
              <input
                type="text"
                name="name"
                value={newPlan.name}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="form-group">
              <label>Description</label>
              <textarea
                name="description"
                value={newPlan.description}
                onChange={handleInputChange}
                rows="3"
              />
            </div>
            <div className="form-group">
              <label>Price ($)</label>
              <input
                type="number"
                step="0.01"
                name="price"
                value={newPlan.price}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="form-group">
              <label>Duration (days)</label>
              <input
                type="number"
                name="duration_days"
                value={newPlan.duration_days}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="form-group">
              <label>Features (comma separated)</label>
              <textarea
                name="features"
                value={newPlan.features}
                onChange={handleInputChange}
                placeholder="Feature 1, Feature 2, Feature 3"
                rows="3"
              />
            </div>
            <button type="submit" className="submit-btn">Create Plan</button>
          </form>
        </div>
      ) : (
        <div className="plan-form-container">
          <h3>Edit Plan</h3>
          <form onSubmit={handleUpdatePlan} className="plan-form">
            <div className="form-group">
              <label>Plan Name</label>
              <input
                type="text"
                name="name"
                value={editingPlan.name}
                onChange={(e) => handleInputChange(e, true)}
                required
              />
            </div>
            <div className="form-group">
              <label>Description</label>
              <textarea
                name="description"
                value={editingPlan.description}
                onChange={(e) => handleInputChange(e, true)}
                rows="3"
              />
            </div>
            <div className="form-group">
              <label>Price ($)</label>
              <input
                type="number"
                step="0.01"
                name="price"
                value={editingPlan.price}
                onChange={(e) => handleInputChange(e, true)}
                required
              />
            </div>
            <div className="form-group">
              <label>Duration (days)</label>
              <input
                type="number"
                name="duration_days"
                value={editingPlan.duration_days}
                onChange={(e) => handleInputChange(e, true)}
                required
              />
            </div>
            <div className="form-group">
              <label>Features (comma separated)</label>
              <textarea
                name="features"
                value={editingPlan.features}
                onChange={(e) => handleInputChange(e, true)}
                rows="3"
              />
            </div>
            <div className="form-actions">
              <button type="submit" className="submit-btn">Update Plan</button>
              <button type="button" className="cancel-btn" onClick={cancelEditing}>
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}

      <div className="plans-table-container">
        <h3>All Plans</h3>
        {plans.length > 0 ? (
          <table className="plans-table">
            <thead>
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Price</th>
                <th>Duration</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {plans.map(plan => (
                <tr key={plan.id} className={!plan.is_active ? 'inactive-plan' : ''}>
                  <td>{plan.id}</td>
                  <td>{plan.name}</td>
                  <td>${plan.price.toFixed(2)}</td>
                  <td>{plan.duration_days} days</td>
                  <td>
                    <span className={plan.is_active ? 'status-active' : 'status-inactive'}>
                      {plan.is_active ? 'Active' : 'Inactive'}
                    </span>
                  </td>
                  <td className="actions-cell">
                    <button onClick={() => startEditing(plan)} className="edit-btn">
                      Edit
                    </button>
                    <button 
                      onClick={() => handleToggleActive(plan.id, plan.is_active)}
                      className={plan.is_active ? 'deactivate-btn' : 'activate-btn'}
                    >
                      {plan.is_active ? 'Deactivate' : 'Activate'}
                    </button>
                    <button onClick={() => handleDeletePlan(plan.id)} className="delete-btn">
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No plans found.</p>
        )}
      </div>
    </div>
  );
};

export default ManagePlans;
