import React, { useState, useEffect } from 'react';
import api from '../services/api';

const UserSubscriptions = () => {
  const [subscriptions, setSubscriptions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchSubscriptions = async () => {
      try {
        const response = await api.get('/subscriptions');
        setSubscriptions(response.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch subscriptions');
        setLoading(false);
      }
    };

    fetchSubscriptions();
  }, []);

  const handleCancel = async (subscriptionId) => {
    try {
      await api.delete(`/subscriptions/${subscriptionId}`);
      setSubscriptions(subscriptions.filter(sub => sub.id !== subscriptionId));
    } catch (err) {
      setError('Failed to cancel subscription');
    }
  };

  if (loading) return <div className="loading">Loading subscriptions...</div>;
  if (error) return <div className="error-message">{error}</div>;

  return (
    <div className="subscriptions-container">
      <h2>My Subscriptions</h2>
      {subscriptions.length === 0 ? (
        <div className="no-subscriptions">
          <p>You don't have any active subscriptions.</p>
          <a href="/plans" className="link-btn">View Available Plans</a>
        </div>
      ) : (
        <div className="subscriptions-list">
          {subscriptions.map((subscription) => (
            <div key={subscription.id} className="subscription-card">
              <h3>{subscription.plan_name}</h3>
              <div className="subscription-dates">
                <p>Start: {new Date(subscription.start_date).toLocaleDateString()}</p>
                <p>Expires: {new Date(subscription.end_date).toLocaleDateString()}</p>
              </div>
              <div className="subscription-status">
                Status: <span className={subscription.is_active ? 'active' : 'inactive'}>
                  {subscription.is_active ? 'Active' : 'Inactive'}
                </span>
              </div>
              {subscription.is_active && (
                <button 
                  className="cancel-btn" 
                  onClick={() => handleCancel(subscription.id)}
                >
                  Cancel Subscription
                </button>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default UserSubscriptions;
