import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import api from '../services/api';

const Plans = () => {
  const [plans, setPlans] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { isAuthenticated } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchPlans = async () => {
      try {
        const response = await api.get('/plans');
        setPlans(response.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch plans');
        setLoading(false);
      }
    };

    fetchPlans();
  }, []);

  const handleSubscribe = async (planId) => {
    if (!isAuthenticated) {
      navigate('/login');
      return;
    }

    try {
      await api.post('/subscriptions', { plan_id: planId });
      navigate('/subscriptions');
    } catch (err) {
      setError('Failed to subscribe to plan');
    }
  };

  if (loading) return <div className="loading">Loading plans...</div>;
  if (error) return <div className="error-message">{error}</div>;

  return (
    <div className="plans-container">
      <h2>Available Subscription Plans</h2>
      <div className="plans-grid">
        {plans.map((plan) => (
          <div key={plan.id} className="plan-card">
            <h3>{plan.name}</h3>
            <p className="plan-price">${plan.price}</p>
            <p className="plan-duration">{plan.duration_days} days</p>
            <p className="plan-description">{plan.description}</p>
            <div className="plan-features">
              {plan.features && plan.features.split(',').map((feature, index) => (
                <div key={index} className="feature-item">
                  âœ“ {feature.trim()}
                </div>
              ))}
            </div>
            <button 
              className="subscribe-btn" 
              onClick={() => handleSubscribe(plan.id)}
            >
              Subscribe Now
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Plans;
